import { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import './Paciente.css'
import axios from 'axios'
//http://localhost:4000/api/getPaciente/1

function Paciente() {

  const PatientDetails = ({Id_Paciente}) => { 
  const [Paciente, setPaciente] = useState(null); 

  useEffect(() => {
  // Hacer la petición GET a la API usando Axios
  const fetchPatient = async () => {
    try {
      const response = await fetch(`http://localhost:4000//api/patients/${id}`); // Replace with your API endpoint
      const data = await response.json();
      setPaciente(data);
    } catch (error) {
      console.log(error);
    }
  };

  fetchPatient();
}, [Id_Paciente]);
  
  const fecthPaciente = async() => {
    try{
      const response = await axios.get("http://localhost:4000/api/getPaciente/1"); //Pasa la id de paciente
      setPaciente(response.data);
    }catch(error){
      console.error('error', error);
    }
  }; 

  if(!Paciente){
    return <><div>error...</div></>
  }

  
 /* const filteredPacientes = ((Paciente) => { "http://localhost:4000/api/getPaciente/1"
    const {Nombre, Apellido_Paterno, Apellido_Materno, Edad} = Paciente
  return Nombre, Apellido_Paterno, Apellido_Materno, Edad});*/

  return (
    <>
      
      <div className="paciente container">
        <div className="paciente-header row rounded">
        <ul>
        
          <h3 key={Paciente.Id_Paciente} className="m-0 text-grow-1">  {Paciente.Nombre}</h3>
           {Paciente.edad} 
          </ul>
        </div>

        <div className="renglon1 row w-100">
          <div className="columna col-sm rounded d-flex align-items-center justify-content-center">
            <p className='m-0'> {"Gastritis"}</p>
          </div>
          <div className="columna col-sm rounded d-flex align-items-center justify-content-center">
            <p className='m-0'>aqui va medicamento: {Paciente.medicamento} </p>
          </div>
        </div>

        <div className='renglon2 row w-100'>
          <div className="columna col-sm rounded d-flex align-items-center justify-content-center">
              <p className='m-0'>aqui va aliimentacion: {Paciente.alimentacion}</p>
            </div>
            <div className="columna  col-sm rounded d-flex align-items-center justify-content-center">
              <p className='m-0'>aqui va sueño: {Paciente.sueño}</p>
            </div>
            <div className="columna col-sm rounded d-flex align-items-center justify-content-center">
              <p className='m-0'>aqui va ejercicio: {Paciente.ejercicio} </p>
            </div>
        </div>
      </div>
    </>
  );
}
  
};

export default Paciente;
