import {useRef} from 'react';
import axios from 'axios';
import './Registro.css'



function Registro(){

    const inputRef = useRef(null);

    function handleClick() {
      // üëáÔ∏è access input value
      console.log(inputRef.current.value);
      var Nombre = inputRef.current.value
    }
  
   
  

   
    


   

   
    
    









