import React, {useEffect } from 'react';
import { useState } from 'react';
import axios from 'axios';
import { createRoot } from 'react-dom/client';
import './Registro.css'



function Registro(){

    const [message, setMessage] = useState('');
  
    const [Nombre, setUpdated] = useState(message);
  
    /*const handleChange = (event) => {
      setMessage(event.target.value);
    };
  
    const handleClick = () => {
      //  "message" stores input field value
      setUpdated(message);
    };
   
    console.log(Nombre)


   

   
    
    

    function sendDataToAPI(Nombre) {
        const apiUrl = 'http://localhost:4000/api/addPacienteNom'; // Replace with your API endpoint
      
        // Create the request body with the input data
        const requestBody = {
          "Nombre": Nombre};
      
        // Send the POST request to the API using Axios
        axios.post(apiUrl, requestBody)
          .then(response => {
            // Handle the API response
            console.log('API response:', response.data);
            // Do something with the response data
          })
          .catch(error => {
            // Handle any errors that occurred during the API request
            console.error('API request error:', error);
          });
      }

      sendDataToAPI(Nombre);







      /*document.getElementById('myButton').addEventListener('click', function() {

        
        var inputValue = document.getElementById('myInput').value;
  
        
        var savedValue = inputValue;

        
  
        
        console.log('Saved value:', savedValue);
        // You can perform any other action with the saved value here
      });*/

    

    return(
        <div className="registro-container card-block align">
            <div className="card registro-form d-inline-flex p-2">
              <h1>Registro</h1>

                <form className='form-control-lg'>
                    <div className="row">
                        <div className="col">
                       <input type="text" className='form-control' value = {message} onChange={handleChange} placeholder='Nombre'/>
                        </div>
                        <div className="col">
                            <input type="text" className='form-control' placeholder='Apellido_Paterno'/>
                        </div>
                    </div>
                    <div className="row">
                        <div className="col">
                            <input type="text" className='form-control' placeholder='Apellido_Materno'/>                         
                        </div>
                        <div className="col">
                            <input type="text" className='form-control' placeholder='Fecha_Nacimiento'/>                      
                        </div>
                    </div>
                    <div className="row">
                        <div className="col">
                            <input type="text" className='form-control' placeholder='Usuario...'/>
                        </div>
                        <div className="col">
                            <input type="text" className='form-control' placeholder='Contraseña...'/>
                        </div>
                    </div>
                    <button onClick={handleClick} className='boton'> Submit </button>
                </form>
               
            </div>
        </div>
    )
}

export default Registro;
