import { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import './Paciente.css'
import axios from 'axios'
//http://localhost:4000/api/getPaciente/1

function Paciente() {

  const PatientDetails = ({Id_Paciente}) => { 
  const [Paciente, setPaciente] = useState(null); 

  useEffect(() => {
  // Hacer la petición GET a la API usando Axios
  const fetchPatient = async () => {
    try {
      const response = axios.get("http://localhost:4000/api/getPaciente/1"); // Replace with your API endpoint
      const data = await response.json();
      setPaciente(data);
    } catch (error) {
      console.log(error);
    }
  };

  const fecthMed = async() => {
    try{
      const response = await axios.get("http://localhost:4000/api/getMed/1"); //Pasa la id de paciente
      setPaciente(response.data);
    }catch(error){
      console.error('error', error);
    }
  }; 

  fetchPatient();
  fecthMed();
}, [Id_Paciente]);
  
  const fecthMed = async() => {
    try{
      const response = await axios.get("http://localhost:4000/api/getMed/1"); //Pasa la id de paciente
      setPaciente(response.data);
    }catch(error){
      console.error('error', error);
    }
  }; 

  if(!Paciente){
    return <><div>error...</div></>
  }

  
 /* const filteredPacientes = ((Paciente) => { "http://localhost:4000/api/getPaciente/1"
    const {Nombre, Apellido_Paterno, Apellido_Materno, Edad} = Paciente
  return Nombre, Apellido_Paterno, Apellido_Materno, Edad});*/
  }
  return (
    <>
      
      <div className="paciente container">
        <div className="paciente-header row rounded">
        <ul>
        
          <h3 key={Paciente.Id_Paciente} className="m-0 text-grow-1">  {"Adrian Cavazos Guerra"}</h3>
           {"19"} 
          </ul>
        </div>

        <div className="renglon1 row w-100">
        
        <h5>Enfermedad y medicamento:</h5>

          <div className="columna col-sm rounded d-flex align-items-center justify-content-center">
            <p className='m-0'> {"Gastritis"}</p>
          </div>
          <div className="columna col-sm rounded d-flex align-items-center justify-content-center">
            <p className='m-0'>{"Paracetamol"} </p>
          </div>
        </div>

        <div className='renglon2 row w-100'>
          <div className="columna col-sm rounded d-flex align-items-center justify-content-center">
          <h5>Comida:</h5>
              <p className='m-0'> {"Cereal, "} {"Pollo con arroz, "} {"Pescado"}</p>
            </div>
            <div className="columna  col-sm rounded d-flex align-items-center justify-content-center">
          <h5>Horas de sueño:</h5>
              <p className='m-0'> {"4 horas"}</p>
          <h5>Calidad de sue:</h5>

              <p className='m-0'> {"4 horas"}</p>

            </div>
            <div className="columna col-sm rounded d-flex align-items-center justify-content-center">
          <h5>Tipo de ejercicio y tiempo realizado:</h5>
              <p className='m-0'> {"Anaerobico"} {"45 minutos"}</p>
            </div>
        </div>
      </div>
    </>
  );

  
};

export default Paciente;
