import {useRef} from 'react';
import axios from 'axios';
import './Registro.css'



function Registro(){

    const inputRef = useRef(null);

    function handleClick() {
      
      console.log(inputRef.current.value);
      var Nombre = inputRef.current.value

      function sendDataToAPI(Nombre, Ap) {
        const apiUrl = 'http://localhost:4000/api/addPaciente'; // Replace with your API endpoint
      
        // Create the request body with the input data
        const requestBody = {
          "Nombre": Nombre};
      
        // Send the POST request to the API using Axios
        axios.post(apiUrl, requestBody)
          .then(response => {
            // Handle the API response
            console.log('API response:', response.data);
            // Do something with the response data
          })
          .catch(error => {
            // Handle any errors that occurred during the API request
            console.error('API request error:', error);
          });
      }

      sendDataToAPI(Nombre);
    }
  

    

    return(
        <div className="registro-container card-block align">
            <div className="card registro-form d-inline-flex p-2">
              <h1>Registro</h1>

                <form className='form-control-lg'>
                    <div className="row">
                        <div className="col">
                       <input type="text" className='form-control' ref = {inputRef}  placeholder='Nombre'/>
                        </div>
                        <div className="col">
                            <input type="text" className='form-control' placeholder='Apellido_Paterno'/>
                        </div>
                    </div>
                    <div className="row">
                        <div className="col">
                            <input type="text" className='form-control' placeholder='Apellido_Materno'/>                         
                        </div>
                        <div className="col">
                            <input type="text" className='form-control' placeholder='Fecha_Nacimiento'/>                      
                        </div>
                    </div>
                    <div className="row">
                        <div className="col">
                            <input type="text" className='form-control' placeholder='Usuario...'/>
                        </div>
                        <div className="col">
                            <input type="text" className='form-control' placeholder='ContraseÃ±a...'/>
                        </div>
                    </div>
                    <button onClick={handleClick} className='boton'> Submit </button>
                </form>
               
            </div>
        </div>
    )
}

export default Registro;
